sources.main.scss:
$green: #5cf291;
$gray: #c4c4c4;

.row-50 {
    padding: 0 50px;
}

body, html {
    overflow-x: hidden;
}

h1 {
    font-size: 42px;
    font-weight: bold;
    text-transform: uppercase;

    span {
        font-size: 10px;
        position: absolute;
    }
}

h2 {
    font-size: 32px;
    font-weight: bold;
    text-transform: uppercase;
    margin: 0;
}

p {
    font-size: 24px;
    line-height: 1.38;
    color: #191919;
}

.navbar {
    max-width: 1920px;
    z-index: 500;
    margin: 0 auto;
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    height: 120px;
    display: flex;
    justify-content: space-between;

    &__left-container, &__right-container {
        width: fit-content;
    }

    &__right-container {
        display: flex;
    }

    &__links {
        background-color: black;
        text-transform: uppercase;
        font-weight: bold;
        height: 100%;
        display: flex;
        justify-content: space-between;
    }

    &__links--language {
        background: transparent;
        margin: auto 0;

        a, span {
            color: black;
            margin: auto 2px;
            transition: 0.2s ease-in;
            letter-spacing: 6px;

            &:hover {
                text-decoration: none;
                color: $green;
            }
        }

        .active {
            color: $green;
        }
    }

    &__link {
        margin: auto 20px;

        a {
            color: white;
            transition: 0.2s ease-in;

            &:hover {
                color: $green;
                text-decoration: none;
            }
        }
    }
}

.scrollspy {
    position: fixed;
    z-index: 5;
    top: 50%;
    left: 0;
    transform: translateY(-50%);
    list-style: none;
    margin-left: 0;
    padding-left: 0;
    width: fit-content;

    li {
        margin-bottom: 100px;
        width: 15px;
        height: 5px;
        background-color: $gray;
        transition: 0.2s all ease-in-out;
        cursor: pointer;
    }

    .uk-active {
        background-color: $green;
        width: 35px;
    }

    a {
        width: 50px;
        height: 5px;
        content: '';
        color: black;
    }
}

.hero__background {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: -1;
    background-size: cover;
    object-fit: cover;
    height: auto;
    width: 100%;
}

.hero {
    max-width: 1920px;
    margin: 0 auto;
    margin-bottom: 200px;
    height: 100vh;
    display: flex;
    position: relative;
    #canvasOne {
        position: absolute;
        z-index: -1;
        top: 0;
        right: 0;
        left: 0;
        bottom: 0;
    }

    &__content {
        position: absolute;
        top: 50%;
        transform: translateY(-50%);

        h1 {
            font-size: 62px;
            font-weight: bold;
            margin-bottom: 0;
            position: relative;
        }

        .language {
            color: black;
            font-weight: 600;

            .active {
                color: $green;
            }

            span {
                position: relative;
                margin: 10px;
                top: 0;
                background-color: transparent;
            }

            a {
                color: black;
                text-decoration: none;
            }
        }

        span {
            background: $green;
            position: absolute;
            height: 30px;
            z-index: -2;
            left: 0;
            top: 40px;
            width: 100%;
        }

        h3 {
            margin: 0;
        }
    }

    &__social {
        display: block;
        position: absolute;
        top: 150px;
        transform: translateY(-50%);
        left: 70px;

        a {
            color: black;

            &:hover {
                text-decoration: none;
                transition: 0.2s all ease-in-out;
                color: $green;
            }

            &:first-child {
                margin-right: 10px;
            }

            font-size: 24px;
        }
    }

    &__slider-arrows {
        position: absolute;
        right: 0;
        top: 50%;
        transform: translateY(-50%);
        color: black;
        cursor: pointer;
        font-size: 40px;
    }

    &__text-left {
        margin-top: 30%;

        h1, h2 {
            color: red;
            text-transform: uppercase;
            font-weight: 600;
            margin: 0;
        }
    }

    &__text-right {
        h1 {
            text-transform: uppercase;
            font-weight: 600;
        }

        max-width: 400px;
        margin-top: auto;
        margin-left: auto;
    }
}

.margin-left-30 {
    margin-left: 30px;
}

@keyframes floating {
    from { transform: translate(0,  0px); }
    65%  { transform: translate(0, 15px); }
    to   { transform: translate(0, -0px); }    
}

@keyframes floating-2 {
    from { transform: translate(0,  0px); }
    65%  { transform: translate(0, 20px); }
    to   { transform: translate(0, 0px); }    
}

@keyframes floating-3 {
    from { transform: translate(0,  0px); }
    65%  { transform: translate(0, 25px); }
    to   { transform: translate(0, 0px); }    
}
.post{
    &__header{
        object-fit: cover;
        width: 100%;
        height: 70vh;
        margin: 150px 0;
    }
    &__share{
        position: absolute;
        right: 0;
        font-weight: bold;
        margin-top: 100px;
        padding-right: 50px;
        .row{
            margin: 0;
            flex-direction: row-reverse;

        }
        h4{
            font-weight: 600;
            text-transform: uppercase;
        }
        a{
            margin-left: 15px;
            color: black;
            font-size: 24px;
            &:hover{
                color: $green;
            }
        }
    }
    &__content{
        position: relative;
        max-width: 1440px;
        padding: 0 50px;
        margin: 0 auto; 
        hr{
            border-top: 5px solid black;
        }
        p{
            line-height: 32pt;
            max-width: 930px;
        }
        h1, h4{
            margin: 0;
        }
        
    }
    &__description{
        margin: 100px 0;
    }
}
.blog{
    max-width: 1920px;
    margin: 50px auto;
    &__content{
        margin-top: 0px;
        h1{
            margin-top: 50px !important;
        }
    }
    img{
        max-height: 600px;
        width: 100%;
        height: auto;
        object-fit: cover;
    }
  
    hr{
        max-width: 250px;
        margin: 50px auto;
        border-top: 5px solid black;
    }
    hr.hr-big{
        width: 100%;
        max-width: 100%;
    }
    &__post{
        position: relative;
        display: flex;
       
        img{
            margin-top: 0 !important;
            width: 50%;
            height: 100%;
            object-fit: cover;
        }
        .blog__content{
            padding: 0 50px;
        }
    }
    &--large{
        margin-top: 200px;
        display: block;
        img{
            width: 100%;
        }
        hr{
            max-width: 100%;
            width: 100%;
            margin-top: 100px;
            border-top: 5px solid black;
        }
        .blog__content{
            padding: 0;
        }
    }
    p{
        max-width: 600px;
    }
    a{
        font-size: 32px;
        text-decoration: none;
        color: black;
        text-transform: uppercase;
        letter-spacing: 12px;
        position: relative;
        &:after{
            position: absolute;
            content: '';
            top: 60%;
            right: -20%;
            transform: translateY(-50%);
            height: 2px;
            width: 30px;
            background-color: $green;

        }
        &:hover{
            &:after{
                transition: 0.2s all ease-in-out;
                right: 0;
                width: 100%;
                height: 3px;
            }
        }
    }
    @media screen and (max-width:1920px){
        margin: 50px;
    }
}
.about {
    max-width: 1920px;
    margin: 0 auto;
    display: flex;

    &__text-left {
        margin: auto 0;
        width: 33.3%;
    }

    &__image-right {
        width: 66.6%;
        position: relative;
        top: 0;
        
        :nth-child(1){
            z-index: 5;
            top: 200px;
            height: 200px;
            animation-name: floating;
            animation-duration: 7s;
            animation-iteration-count: infinite;
            animation-timing-function: ease-in-out;
            left: 250px;
        }

        :nth-child(2){
            z-index: 6;
            bottom: 140px;
            top: 0px;
            right: 200px;
            height: 300px;
            animation-name: floating-3;
            animation-duration: 7s;
            animation-iteration-count: infinite;
            animation-timing-function: ease-in-out;
        }
        :nth-child(3){
            z-index: 3;
            top: 50px;
            right: 0;
            bottom: 0;
            left: 100px;
            // animation-name: floating;
            // animation-duration: 7s;
            // animation-iteration-count: infinite;
            // animation-timing-function: ease-in-out;
        }
        img {
            position: absolute;
            width: auto;
            object-fit: cover;
            height: 100%;
        }
    }

    margin-bottom: 300px;
}

.row {
    position: relative;
    margin: 50px;

    &--flex {
        display: flex;

        .column {
            width: 50%;
            display: flex;

            h1 {
                margin: auto 0;
                height: fit-content;
            }
        }
    }
}

.underlay {
    display: none;
    position: fixed;
    background-color: rgba($color: white, $alpha: 0.9);
    z-index: 99;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
}

.portfolio {
    max-width: 1920px;
    margin: 0 auto;
    position: relative;

    &-overlay {
        ion-icon {
            color: black;
            text-align: right;
            width: 100%;
            margin: 0 auto;
            display: none;
            position: fixed;
            right: 0px;
            top: 20px;
            transform: translateY(-50%);
            z-index: 100;
            cursor: pointer;
            font-size: 32px;

            &:hover {
                color: $green;
            }
        }
    }

    img {
        width: 100%;
        object-fit: cover;
        height: 100%;
        cursor: pointer;
        transition: 0.2s all ease-in-out;
        border: 3px solid transparent;

        &:hover {
            position: relative;
            border: 3px solid $green;
        }
    }

    &-content {
        max-width: 1920px;
        display: none;
        z-index: 100;
        background-color: rgba($color: #000000, $alpha: 0.8);
        position: fixed;
        height: 90%;
        margin: 50px auto;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        overflow-y: scroll;
        overflow-x: visible;

        img {
            max-height: 450px;
            width: 100%;
            border: none;
            height: auto;

            &:hover {
                border: none;
            }
        }

        h1, p {
            color: white;
        }

        color: white;

        @media screen and (max-width: 1920px) {
            margin: 50px;
        }
    }

    &__grid {
        display: grid;
        grid-template-columns: repeat(2, 2fr);
        grid-gap: 20px;
        margin-bottom: 50px;
        min-height: 540px;
    }

    &__column {
        grid-gap: 10px;
        display: grid;
        grid-template-columns: repeat(2, 1fr);
    }

    &__content {
        margin: auto 0;
    }
}

::-webkit-scrollbar {
    cursor: pointer;
    width: 5px;
    background-color: white;
}

::-webkit-scrollbar-track {
    background: #f1f1f1;
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: $green;
    width: 100%;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background: black;
    cursor: pointer;
}

.services {
    max-width: 1920px;
    margin: 0 auto;
    margin-top: 130px;

    &__content-row {
        margin-top: 180px;
        display: flex;
    }

    &__content-column {
        width: 50%;
    }

    &__content {
        p{
            font-size: 24px;
            font-weight: normal;
            font-stretch: normal;
            font-style: normal;
            line-height: 32pt;
            letter-spacing: normal;
            text-align: left;
            color: #191919;
            margin-bottom: 50px;
        }
        font-size: 24px;
        font-weight: normal;
        font-stretch: normal;
        font-style: normal;
        line-height: 1.38;
        letter-spacing: normal;
        text-align: left;
        color: #191919;
        display: none;
    }

    .active-content {
        display: block;
    }

    .active-service {
        color: $green;
        text-decoration: line-through;
    }

    h2 {
        cursor: pointer;
        letter-spacing: 12px;
        margin-bottom: 60px;
        transition: 0.1s all ease-in-out;

        &:hover {
            text-decoration: line-through;
            color: $green;
        }
    }

    .logo-list {
        display: flex;
        margin-left: 0;
        padding-left: 0;

        img {
            height: 35px;
            width: 35px;
            object-fit: contain;
            margin-right: 45px;
        }
    }
}

.separator {
    max-width: 1920px;
    margin: 170px auto;
    text-align: center;
}

.work-process {
    max-width: 1920px;
    height: 100%;
    text-align: center;
    position: relative;
    margin: 0 auto;

    h1 {
        text-align: left;
    }

    img {
        margin-bottom: 200px;
        position: relative;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        width: 100%;
    }
}

.faq {
    display: flex;

    &__container-right {
        width: 50%;

        h1 {
            position: relative;
            margin: 0;
            font-size: 110px;
            color: black;
            text-transform: none;
            line-height: 110px;
        }

        .block-container {
            background: $green;
            width: 100%;
            height: 230px;
            margin-top: 25px;
        }
    }

    &__container-left {
        position: relative;
        margin-right: 25px;
        width: 50%;
        background: black;

        p, h1, h3 {
            color: white;
        }

        h1 {
            position: absolute;
            top: 50px;
        }

        h3 {
            position: relative;
        }
    }

    &__questions {
        max-width: 600px;
        padding: 0px 20px;
        margin: 190px auto;
    }

    &__question h3 {
        display: flex;
        justify-content: space-between;
        padding-bottom: 15px;
        padding-right: 30px;
        border-bottom: 1px solid white;
        transition: 0.1s all ease-in-out;
        cursor: pointer;

        &:hover {
            color: $green;
            border-bottom: 1px solid $green;
        }
    }

    &__question p {
        margin-bottom: 50px;
        display: none;
    }
}

.contact {
    margin: 200px auto;
    max-width: 1920px;
    display: flex;
    padding: 0 50px;
    position: relative;

    .vertical-separator {
        width: 2px;
        height: 100%;
        display: block;
        position: absolute;
        left: 50%;
        background-color: black;
        top: 50%;
        transform: translate(-50%, -50%);
    }

    .column-left {
        width: 50%;
    }

    .column-right {
        width: 50%;
        margin: 0 auto;
        max-width: 700px;

        h1 {
            text-transform: none;
        }

        .half-inputs {
            display: flex;

            input {
                width: 50%;
            }

            :nth-child(2) {
                position: relative;
                left: 20px;
                margin-left: 30px;
            }
        }

        form {
            margin-top: 130px;

            input {
                width: 100%;
                border: none;
                border-bottom: 1px solid black;
                padding: 10px;
                transition: 0.1s all ease-in-out;
                margin-bottom: 20px;
                outline: none;

                &:focus {
                    border-bottom: 1px solid $green;
                }
            }

            textarea {
                padding: 10px;
                transition: 0.1s all ease-in-out;
                width: 100%;
                outline: none;
                border: none;
                border-bottom: 1px solid black;

                &:focus {
                    border-bottom: 1px solid $green;
                }
            }

            button {
                margin-top: 50px;
                background-color: black;
                color: white;
                transition: 0.1s all ease-in-out;
                border: none;
                padding: 15px 30px;
                cursor: pointer;

                &:hover {
                    background: $green;
                }
            }
        }
    }

    .title {
        h1 {
            margin-bottom: 0;
        }

        .wrapped-hr {
            display: flex;

            h1 {
                width: fit-content;
                margin: 0;
            }
        }

        .hr-middle {
            display: block;
            width: 130px;
            height: 5px;
            margin-right: 10px;
            background: $green;
            top: 25px;
            position: relative;
        }
    }

    .content {
        margin: 75px 0px;
        max-width: 640px;
    }

    a {
        letter-spacing: 4px;
        font-size: 24px;
        color: black;

        &:hover {
            text-decoration: none;
            color: $green;
        }
    }

    ion-icon {
        font-size: 32px;
        margin-right: 35px;
        color: black;
    }

    ul {
        padding-left: 0;
    }

    ul li {
        margin-bottom: 35px;
        font-size: 32px;
        list-style: none;
    }
}

.footer {
    background: #191919;
    color: white;
    max-width: 1920px;
    margin: 0 auto;

    a, p, h4 {
        color: white;
    }

    .about-section-right img {
        height: 40px;
        width: 40px;
        object-fit: contain;
    }

    .about-section p {
        margin-bottom: 0;
        max-width: 300px;
        margin-top: 30px;
    }

    h4 {
        font-weight: bold;
    }

    ul {
        margin: 0;
        padding-left: 0;
    }

    li {
        list-style: none;
    }

    a {
        transform: 0.2s all ease-in-out;
        color: #bbbbbb;

        &:hover {
            color: $green;
            text-decoration: none;
        }
    }

    &__green-border {
        background-color: $green;
        height: 150px;
        display: block;
        max-width: 1920px;
        margin: 0 auto;
    }

    &__content {
        padding: 100px 0px;
        display: flex;
        justify-content: space-between;
    }

    .about-section, .summary-section, .terms-section, .social-section {
        margin: auto 20px;
        width: 25%;
    }

    p {
        color: #bbbbbb;
        font-size: 16px;
    }

    .social-section {
        ion-icon {
            position: relative;
            top: 3px;
        }
    }

    .copyright {
        font-size: 12px;
        letter-spacing: 2px;
        color: white;
        margin: 10px 0px;
        position: relative;
        top: 15px;
    }
}

.footer_green-border {
    background-color: $green;
    max-width: 1920px;
    padding: 0 50px;
    margin: 0 auto;
    height: 50px;
    display: block;
    margin-top: 100px;
}

.scroll-downs {
    position: absolute;
    right: 0;
    bottom: 100px;
    left: 0;
    margin: auto;
    width: 34px;
    height: 55px;
}

.mousey {
    width: 3px;
    padding: 10px 15px;
    height: 35px;
    border: 2px solid black;
    border-radius: 25px;
    opacity: 0.75;
    box-sizing: content-box;
}

.scroller {
    width: 3px;
    height: 10px;
    border-radius: 25%;
    background-color: black;
    animation-name: scroll;
    animation-duration: 2.2s;
    animation-timing-function: cubic-bezier(0.15, 0.41, 0.69, 0.94);
    animation-iteration-count: infinite;
}

@keyframes scroll {
    0% {
        opacity: 0;
    }

    10% {
        transform: translateY(0);
        opacity: 1;
    }

    100% {
        transform: translateY(15px);
        opacity: 0;
    }
}
