    .mobile-content {
        display: none;
    }
}

.services .mobile-content {}

.active-service .mobile-content {
    display: block !important;
}

.services__content {
    display: none;
}

.services__content.active-content {
    display: block;
}

.separator {
    max-width: 1920px;
    margin: 170px auto;
    text-align: center;
}

.work-process {
    max-width: 1920px;
    height: 100%;
    text-align: center;
    position: relative;
    margin: 0 auto;

    h1 {
        text-align: left;
    }

    img {
        margin-bottom: 200px;
        position: relative;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        width: 100%;
    }
}

.faq {
    display: flex;

    &__container-right {
        width: 50%;

        h1 {
            position: relative;
            margin: 0;
            font-size: 110px;
            color: black;
            text-transform: none;
            line-height: 100px;
        }

        .block-container {
            background: $green;
            width: 100%;
            height: 230px;
            margin-top: 25px;
        }
    }

    &__container-left {
        position: relative;
        margin-right: 25px;
        width: 50%;
        background: black;

        p, h1, h3 {
            color: white;
        }

        h1 {
            position: absolute;
            top: 50px;
        }

        h3 {
            position: relative;
        }
    }

    &__questions {
        max-width: 600px;
        padding: 0px 20px;
        margin: 190px auto;
    }

    &__question h3 {
        display: flex;
        justify-content: space-between;
        padding-bottom: 15px;
        padding-right: 30px;
        border-bottom: 1px solid white;
        transition: 0.1s all ease-in-out;
        cursor: pointer;

        &:hover {
            color: $green;
            border-bottom: 1px solid $green;
        }
    }

    &__question p {
        margin-bottom: 50px;
        display: none;
    }
}

.contact {
    margin: 200px auto;
    max-width: 1920px;
    display: flex;
    padding: 0 100px;
    position: relative;

    .vertical-separator {
        width: 2px;
        height: 100%;
        display: block;
        position: absolute;
        left: 50%;
        background-color: black;
        top: 50%;
        transform: translate(-50%, -50%);
    }

    .column-left {
        width: 45%;
        margin: 0 auto;
        position: relative;

        &:after {
            content: '';
            position: absolute;
            height: 100%;
            width: 2px;
            right: -20px;
            top: 0;
            bottom: 0;
            background-color: black;
        }
    }

    .column-right {
        width: 45%;
        margin: 0 auto;
        max-width: 700px;

        h1 {
            text-transform: none;
        }

        .half-inputs {
            display: flex;

            input {
                width: 50%;
            }

            :nth-child(2) {
                position: relative;
                left: 20px;
                margin-left: 30px;
            }
        }

        form {
            margin-top: 130px;

            input {
                width: 100%;
                border: none;
                border-bottom: 1px solid black;
                padding: 10px;
                transition: 0.1s all ease-in-out;
                margin-bottom: 20px;
                outline: none;

                &:focus {
                    border-bottom: 1px solid $green;
                }
            }

            textarea {
                padding: 10px;
                transition: 0.1s all ease-in-out;
                width: 100%;
                outline: none;
                border: none;
                border-bottom: 1px solid black;

                &:focus {
                    border-bottom: 1px solid $green;
                }
            }

            button {
                margin-top: 50px;
                background-color: black;
                color: white;
                transition: 0.1s all ease-in-out;
                border: none;
                padding: 15px 30px;
                cursor: pointer;

                &:hover {
                    background: $green;
                }
            }
        }
    }

    .title {
        h1 {
            margin-bottom: 0;
        }

        .wrapped-hr {
            display: flex;

            h1 {
                width: fit-content;
                margin: 0;
            }
        }

        .hr-middle {
            display: block;
            width: 130px;
            height: 5px;
            margin-right: 10px;
            background: $green;
            top: 25px;
            position: relative;
        }
    }

    .content {
        margin: 75px 0px;
        max-width: 640px;
    }

    a {
        letter-spacing: 4px;
        font-size: 24px;
        color: black;

        &:hover {
            text-decoration: none;
            color: $green;
        }
    }

    ion-icon {
        font-size: 32px;
        margin-right: 35px;
        color: black;
    }

    ul {
        padding-left: 0;
    }

    ul li {
        margin-bottom: 35px;
        font-size: 32px;
        list-style: none;
    }
}

.footer {
    background: #191919;
    color: white;
    max-width: 1920px;
    margin: 0 auto;

    a, p, h4 {
        color: white;
    }

    .about-section-right img {
        height: 40px;
        width: 40px;
        object-fit: contain;
    }
    .about-section  img{
        width: 100px;
    }
    .about-section p {
        margin-bottom: 0;
        max-width: 300px;
        margin-top: 30px;
    }

    h4 {
        font-weight: bold;
    }

    ul {
        margin: 0;
        padding-left: 0;
    }

    li {
        list-style: none;
    }

    a {
        transform: 0.2s all ease-in-out;
        color: #bbbbbb;

        &:hover {
            color: $green;
            text-decoration: none;
        }
    }

    &__green-border {
        background-color: $green;
        height: 150px;
        display: block;
        max-width: 1920px;
        margin: 0 auto;
    }

    &__content {
        padding: 100px 0px;
        display: flex;
        justify-content: space-between;
    }

    .about-section, .summary-section, .terms-section, .social-section {
        margin: auto 20px;
        width: 25%;
    }

    p {
        color: #bbbbbb;
        font-size: 16px;
    }

    .social-section {
        ion-icon {
            position: relative;
            top: 3px;
        }
    }

    .copyright {
        font-size: 12px;
        letter-spacing: 2px;
        color: white;
        margin: 10px 0px;
        position: relative;
        top: 15px;
    }
}

.footer_green-border {
    background-color: $green;
    max-width: 1920px;
    padding: 0 100px;
    margin: 0 auto;
    height: 50px;
    display: block;
    margin-top: 100px;
}

.scroll-downs {
    position: absolute;
    right: 0;
    bottom: 100px;
    left: 0;
    margin: auto;
    width: 34px;
    height: 55px;
}

.mousey {
    width: 3px;
    padding: 10px 15px;
    height: 35px;
    border: 2px solid black;
    border-radius: 25px;
    opacity: 0.75;
    box-sizing: content-box;
}

.scroller {
    width: 3px;
    height: 10px;
    border-radius: 25%;
    background-color: black;
    animation-name: scroll;
    animation-duration: 2.2s;
    animation-timing-function: cubic-bezier(0.15, 0.41, 0.69, 0.94);
    animation-iteration-count: infinite;
}

@keyframes scroll {
    0% {
        opacity: 0;
    }

    10% {
        transform: translateY(0);
        opacity: 1;
    }

    100% {
        transform: translateY(15px);
        opacity: 0;
    }
}

@media screen and (max-width: 767px) {
    p {
        font-size: 20px;
    }
}

