        }
    }

    .contact {
        flex-wrap: wrap;
        text-align: center;
        margin: 100px 20px;

        h1 {
            font-size: 32px;
        }

        .title .hr-middle {
            display: block;
            width: 90px;
            height: 5px;
            margin-right: 10px;
            background: #5cf291;
            top: 16px;
            position: relative;
        }

        .column-left {
            width: 100%;

            &::after {
                display: none;
            }
        }

        .content {
            margin: 50px 0;
        }

        .column-right {
            width: 100%;

            .form {
                margin-top: 30px;
            }

            .half-inputs {
                flex-wrap: wrap;

                input {
                    width: 100%;
                }
            }
        }

        .wrapped-hr {
            width: fit-content;
            margin: auto;
        }

        .info li {
            line-height: 30px;

            ion-icon {
                margin-right: 0;
                width: 100%;
            }
        }
    }

    .contact .column-right .half-inputs :nth-child(2) {
        margin-left: 0;
        left: 0;
    }

    .contact .column-right form input {
        padding: 10px 0;
    }

    .contact .column-right form textarea {
        padding: 10px 0;
    }

    .contact .column-right form {
        margin-top: 30px;
    }

    .footer {
        &__content {
            flex-wrap: wrap;
        }

        .about-section, .summary-section, .terms-section, .social-section {
            width: 100%;
            margin-bottom: 20px;
        }
    }
}

