}

.about {
    max-width: 1920px;
    margin: 0 auto;
    display: flex;

    &__text-left {
        margin: auto 0;
        width: 33.3%;
    }

    &__image-right {
        margin: auto 0;
        width: 66.6%;

        img {
            width: 100%;
        }
    }

    margin-bottom: 300px;
}

.portfolio {
    max-width: 1920px;
    margin: 0 auto;

    &__grid {
        display: grid;
        grid-template-columns: repeat(2, 2fr);
        grid-gap: 20px;
        margin-bottom: 50px;
        min-height: 540px;
    }

    &__column {
        grid-gap: 10px;
        display: grid;
        grid-template-columns: repeat(2, 1fr);
    }

    &__content {
        margin: auto 0;
    }

    img {
        width: 100%;
        object-fit: cover;
        height: 100%;
    }
}

.services {
    max-width: 1920px;
    margin: 0 auto;
    margin-top: 130px;

    &__content-row {
        margin-top: 180px;
